mapper.insert:
insert into Course (id, type, grade_id, 
  subject_id, title, catalog, 
  detail, price, resource_url, 
  add_time, update_time, delete_sign
  )
values (#{id,jdbcType=INTEGER}, #{type,jdbcType=INTEGER}, #{gradeId,jdbcType=INTEGER}, 
  #{subjectId,jdbcType=INTEGER}, #{title,jdbcType=VARCHAR}, #{catalog,jdbcType=VARCHAR}, 
  #{detail,jdbcType=VARCHAR}, #{price,jdbcType=INTEGER}, #{resourceUrl,jdbcType=VARCHAR}, 
  #{addTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{deleteSign,jdbcType=INTEGER}
  )

mapper.select:
select

from Course
where delete_sign = 0

  and type = #{type,jdbcType=INTEGER}


  and grade_id = #{gradeId,jdbcType=INTEGER}


  and subject_id = #{subjectId,jdbcType=INTEGER}


  and title like CONCAT('%', #{title,jdbcType=VARCHAR}, '%')


  and catalog like CONCAT('%', #{catalog,jdbcType=VARCHAR}, '%')


  and detail like CONCAT('%', #{detail,jdbcType=VARCHAR}, '%')


  and price = #{price,jdbcType=INTEGER}


  and resource_url like CONCAT('%', #{resourceUrl,jdbcType=VARCHAR}, '%')


order by id desc

  limit #{offset, jdbcType=INTEGER}, #{pageSize, jdbcType=INTEGER}
