mapper.select:
select

from Preferential
where delete_sign = 0

    and type = #{type,jdbcType=INTEGER}


    and limit_value = #{limitValue,jdbcType=INTEGER}


    and rule = #{rule,jdbcType=INTEGER}


    and detail like CONCAT('%', #{detail,jdbcType=VARCHAR}, '%')


order by id desc

  limit #{offset, jdbcType=INTEGER}, #{pageSize, jdbcType=INTEGER}
